import { useEffect, useState } from "react";
import axios from "axios";
import { auth } from "../firebase";
import { useNavigate } from "react-router-dom";
import { Container, Table, Button, Spinner } from "react-bootstrap";

const Dashboard = () => {
  const [patients, setPatients] = useState([]); // Ensure it's always an array
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((currentUser) => {
      if (!currentUser) {
        navigate("/");
      }
    });

    return () => unsubscribe();
  }, [navigate]);

  // Fetch patient data from backend and ensure it's an array
  useEffect(() => {
    axios.get("http://localhost:5000/api/patients/")
      .then((response) => {
        console.log("API Response:", response.data); // Debugging
        if (Array.isArray(response.data)) {
          setPatients(response.data);
        } else {
          console.error("API did not return an array:", response.data);
          setPatients([]); // Prevent errors if response is incorrect
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching patients:", error);
        setPatients([]); // Fallback to empty array
        setLoading(false);
      });
  }, []);

  return (
    <Container className="mt-5">
      <h1 className="mb-4">Patient Dashboard</h1>
      {loading ? (
        <Spinner animation="border" />
      ) : (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Patient ID</th>
              <th>Name</th>
              <th>Age</th>
              <th>BP</th>
              <th>Sugar</th>
              <th>Heart Rate</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {Array.isArray(patients) && patients.length > 0 ? (
              patients.map((patient) => (
                <tr key={patient.patientID}>
                  <td>{patient.patientID}</td>
                  <td>{patient.name}</td>
                  <td>{patient.age}</td>
                  <td>{patient.bp}</td>
                  <td>{patient.sugar}</td>
                  <td>{patient.heartRate}</td>
                  <td>
                    <Button variant="warning" onClick={() => alert("Edit feature coming soon!")}>
                      Edit
                    </Button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="7" className="text-center">No patient data available</td>
              </tr>
            )}
          </tbody>
        </Table>
      )}
    </Container>
  );
};

export default Dashboard;
