import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Container, Form, Button } from "react-bootstrap";

const PatientLogin = () => {
  const [credentials, setCredentials] = useState({ patientID: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/auth/patient/login", credentials);
      alert("Login successful!");
      navigate(`/patient-dashboard/${credentials.patientID}`); // Redirect to specific patient's dashboard
    } catch (error) {
      alert("Invalid credentials. Please try again.");
    }
  };

  return (
    <Container>
      <h2>Patient Login</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Label>Patient ID</Form.Label>
          <Form.Control type="text" name="patientID" onChange={handleChange} required />
        </Form.Group>
        <Form.Group>
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" name="password" onChange={handleChange} required />
        </Form.Group>
        <Button type="submit">Login</Button>
      </Form>
    </Container>
  );
};

export default PatientLogin;
