import { useContext, useState } from "react";
import { PatientContext } from "../context/PatientContext";
import { Container, Card, Button, Form } from "react-bootstrap";
import { toast } from "react-toastify";

const Dashboard = () => {
  const { patients, addPatient } = useContext(PatientContext);
  const [name, setName] = useState("");
  const [bp, setBp] = useState("");
  const [sugar, setSugar] = useState("");

  const handleAddPatient = () => {
    if (!name || !bp || !sugar) {
      toast.error("Please fill in all fields!");
      return;
    }
    addPatient({ name, bp, sugar });
    toast.success("Patient Added Successfully!");
    setName("");
    setBp("");
    setSugar("");
  };

  const startListening = () => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.lang = "en-US";
    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      toast.info(`You said: ${transcript}`);
    };
    recognition.start();
  };

  return (
    <Container className="mt-5">
      <h2>Patient List</h2>
      <Form className="mb-4">
        <Form.Group>
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mt-3">
          <Form.Label>Blood Pressure</Form.Label>
          <Form.Control
            type="text"
            value={bp}
            onChange={(e) => setBp(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mt-3">
          <Form.Label>Blood Sugar Level</Form.Label>
          <Form.Control
            type="text"
            value={sugar}
            onChange={(e) => setSugar(e.target.value)}
          />
        </Form.Group>
        <Button className="mt-3" variant="success" onClick={handleAddPatient}>
          Add Patient
        </Button>
        <Button className="mt-3 ms-2" variant="info" onClick={startListening}>
          ðŸŽ™ Start Voice Input
        </Button>
      </Form>

      {patients.length === 0 ? (
        <p>No patients added yet.</p>
      ) : (
        patients.map((patient, index) => (
          <Card key={index} className="mb-3 p-3">
            <h5>{patient.name}</h5>
            <p>BP: {patient.bp}</p>
            <p>Sugar Level: {patient.sugar}</p>
          </Card>
        ))
      )}
    </Container>
  );
};

export default Dashboard;
